.p-wrapper-home {
            max-width: 1950px;
            margin-left: auto;
            margin-right: auto;
        }
        .js-user-slider {
            transition: width 0.2s, height 0.2s;
        }
        .js-user-slider span {
            display: none;
            opacity: 0;
        }
        .js-user-slider span:first-of-type {
            display: block;
            opacity: 1;
        }
        .js-user-slide-init {
            transform: translate3d(0, -100%, 0);
        }
        .js-user-slider.js-loaded span {
            display: block;
            position: absolute;
            top: 100%;
            left: auto;
            right: auto;
        }
        .js-user-slide-in {
            animation: slideIn 1s forwards;
        }
        @keyframes slideIn {
            0% {
                opacity: 0;
		        transform: translate3d(0, -150%, 0);
            }

            100% {
                opacity: 1;
                transform: translate3d(0, -100%, 0);
            }
        }
        .js-user-slide-out {
            animation: slideOut 1s forwards;
        }
        @keyframes slideOut {
            0% {
                opacity: 1;
		        transform: translate3d(0, -100%, 0);
            }

            30% {
                opacity: 0;
            }

            100% {
                opacity: 0;
                transform: translate3d(0, -50%, 0);
            }
        }

        .js-use-case p {
            display: none;
        }
        .js-use-case.js-use-case--is-active p {
            display: block;
        }
        .js-use-case.js-use-case--is-active h3 {
            border-top-color: var(--black-500) !important;
        }
        .js-use-case.js-use-case--is-active svg {
            color: var(--blue-500) !important;
