(function () {
  var sections = document.querySelectorAll('.js-feature-section');

  function updateNav() {
    for (var i = 0; i < sections.length; ++i) {
      var el = sections[i];
      var bodyRect = document.body.getBoundingClientRect();
      var elRect = el.getBoundingClientRect();
      var offsetTop = (elRect.top - bodyRect.top) - 300;
      var offsetBottom = (elRect.top - bodyRect.top + elRect.height) - 300;
      var scrollPosition = document.documentElement.scrollTop || document.body.scrollTop;

      // Nav item
      var current = document.querySelector('#feature-nav a[href="#' + el.id + '"]');

      if (current) {
        // Enters view
        if (offsetTop <= scrollPosition) {
          current.classList.add('is-selected');
        }
        // Leaves view
        if (offsetBottom <= scrollPosition || offsetTop >= scrollPosition) {
          current.classList.remove('is-selected');
        }
      }
    }
  }

  function throttle(event, callback) {
    var timer = false;
    window.addEventListener(event, function () {
      if (timer) {
        clearTimeout(timer);
      }
      timer = setTimeout(function () {
        callback();
      }, 100);
    }, false);
  }

  throttle('scroll', updateNav);
  throttle('resize', updateNav);
})();

