$(document).ready(function () {
        $('body').on('click', '.newTabAndRefresh', function (e) {
            e.preventDefault()
            // Open new tab
            window.open($(this).attr('href'), '_blank');
            // reload current page after waiting for 2 seconds
            setTimeout(function(){location.reload();}, 2000);
        })
    })

