script:
$(function () {
        $('.js-tag-lang').change(function () {
            $('.js-save-tag-lang-button').prop('disabled', false);
        });
    });

    $('.js-save-tag-lang-button').click(function () {
        var confirmMessage = $(this).attr('data-confirm');
        var tagId = $(this).attr('data-tag-id');
        var language = $('.js-tag-lang').val();

        if (confirmMessage && !confirm(confirmMessage)) { return; }

        $.post("/tags/edit-tag-lang",
            { fkey: StackExchange.options.user.fkey, id: tagId, value: language },
            function () {
                $('.js-save-tag-lang-button').prop('disabled', true);
            });
    });

StackExchange.ready(function() {

                var ga3Settings = {
                    autoLink: ["stackoverflow.blog","info.stackoverflowsolutions.com","stackoverflowsolutions.com"],
                    sendTitles: true,
                    tracker: window.ga,
                    trackingCodes: [
                        'UA-108242619-1'
                    ],
                    checkDimension: 'dimension42'
                                                    };

                var customGA4Dimensions = {};


                var ga4Settings = {
                    tracker: gtag,
                    trackingCodes: [
                        'G-WCZ03SZFCQ'
                    ],
                    consentsToPerformanceCookies: "denied",
                    consentsToTargetingCookies: "denied",
                    eventParameters: customGA4Dimensions,
                    checkForAdBlock: true
                };

                StackExchange.ga.init({ GA3: ga3Settings, GA4: ga4Settings });




                StackExchange.ga.setDimension('dimension3', 'Tags/TagWikiImpl');


                StackExchange.ga.setDimension('dimension7', "1686685751.1012987262");

                StackExchange.ga.trackPageView();
            });
